-- Seed MK Seasons Data (Complete with all 47 seasons)
-- Run this script in your Supabase SQL Editor to populate the mk_seasons_data table
-- Seasons 24-33 will have the same data as Season 23

-- Temporarily disable RLS to allow bulk insert
ALTER TABLE public.mk_seasons_data DISABLE ROW LEVEL SECURITY;

-- Insert all MK seasons data (seasons 24-33 use season 23 data)
ON CONFLICT (item_name) DO UPDATE SET
  season_values = EXCLUDED.season_values,
  season_quantities = EXCLUDED.season_quantities,
  updated_at = timezone('utc'::text, now());

-- Re-enable RLS
ALTER TABLE public.mk_seasons_data ENABLE ROW LEVEL SECURITY;

-- Verify the data was inserted
SELECT COUNT(*) as total_items FROM public.mk_seasons_data;

-- Show a sample of the data with seasons 24-33 included
SELECT item_name, 
       season_values[23] as season_23_value,
       season_values[24] as season_24_value, 
       season_values[25] as season_25_value,
       season_values[33] as season_33_value,
       season_values[34] as season_34_value
FROM public.mk_seasons_data 
LIMIT 5;
